import axios from "axios";
import {useState, useEffect} from "react"
import  './MyRecipe.css'
function MyRecipes( ) {
    const [data, setData] = useState([]);
  const userId = localStorage.getItem("id")
    useEffect(() => {
        const fetchData = async () => {
            const response = await axios.get(`http://localhost:1112/recipe`);

            await Promise.all(
              response.data.map((item) =>
                axios.get(`http://localhost:1112/user/${item.user}`)
              )
            );

            const userId = localStorage.getItem("id");
            const userRecipes = response.data.filter((item) => item.user === userId);

            setData(userRecipes);
          };  
      fetchData();
    }, [userId]);


    const handleImageClick = (index) => {
      setShowPopup(true);
      setCardData(data[index]);
    };

    const handleClosePopup = () => {
      setShowPopup(false);
    };
    return (
      <div className="MyRecipeProfileSectiom">
         {data.map((item, index) => (
          <div key={index} className="MyRecipeCard">
            <p className="MyRecipeCardTitle">{item.title}</p>
              <img
                src={item.image}
                alt="main Recipe"
                className="MyRecipeCardImage"
                onClick={() => handleImageClick(index)}
              />
            </div>
        ))}
         <CSSTransition
        in={showPopup}
        timeout={300}
        classNames="Popup"
        unmountOnExit
      >
        <div className="Popup">
          <div className="RecipeCArd">
            <img
              src={cardData.image}
              alt="Card"
              className="RecipeCardPopImage"
            />
            <div className="RecipeCArdContent">
              <header className="UserHeaderCard">
                <div className="UserHeaderCardImgName">
                  <img src={cardData.userimg} alt="" />
                  <h1>{cardData.username}</h1>
                </div>
                <h2 onClick={handleClosePopup} className="CardCloseButton">
                  close
                </h2>
              </header>
              <hr />
              <h1>{cardData.title}:</h1>
              <p>{cardData.description}</p>
              <hr />
              <h1>Ingredients:</h1>
              {cardData.ingredients &&
                cardData.ingredients.map((ingredient, index) => (
                  <li key={index}>{ingredient}</li>
                ))}
              <hr />
              <h1>How to apply the recipe:</h1>
              <p>{cardData.steps}</p>
            </div>
          </div>
        </div>
      </CSSTransition>
      </div>
    );
  }

  export default MyRecipes;
