import { NavLink} from "react-router-dom";
import "./Navbar.css";
import logo from "../../assets/cookieLogo.png";
import { ToastContainer } from "react-toastify";
function NavigationBar() {
  const isAdmin = localStorage.getItem("isAdmin") === "true";
  const handleLogout = () => {
    window.location.href = "/Login";
    localStorage.removeItem("token");
    localStorage.removeItem("name");
    localStorage.removeItem("isAdmin");
    localStorage.removeItem("id");
    localStorage.clear();
  };

  const isLoggedIn = window.localStorage.token;

  const login = () => {
    if (!isLoggedIn) {
      return (
        <>
          <NavLink
          //  className="Logout-LoginButton" 
           to="/Login">
            Login/Signup
          </NavLink>
        </>
      );
    } else {
      return (
        <NavLink
          // className="Logout-LoginButton Logout-LoginButtonLogout"
          onClick={() => handleLogout()}
        >
          Logout
        </NavLink>
      );
    }
  };
  return (
    <>
      <nav className="NavigatorBar">
        <NavLink to="/" className="NavlogoA">
          <img src={logo} alt="" className="Navlogo" />
        </NavLink>
        <div className="PageNavigator ">
          <NavLink class="button-87" to="/">Home</NavLink>
          <NavLink to="/search" style={{ display: "none" }}>
            Search
          </NavLink>
          <NavLink to="/challenge" style={{ display: "none" }}>
            Challenge
          </NavLink>
          <div className="LoginNavPhone Logout-LoginButtonLogout">
            {login()}
          </div>

          {isLoggedIn ? <NavLink to="/profile">Profile</NavLink> : null}
          {isAdmin ? <NavLink to="/dashboard">Dashboard</NavLink> : null}
        </div>

        <div className="login-LoginButtonLogout">{login()}</div>
      </nav>

      <ToastContainer />
    </>
  );
}

export default NavigationBar;
