import { Navigate, NavLink } from "react-router-dom";
import "./Navbar.css";
import useToken from "./useToken";

function NavigationBar() {
  const isAdmin = window.localStorage.getItem("isAdmin") === "true";
  const { setToken } = useToken();

  const checkAdminAccess = (element) => {
    if (isAdmin) {
      return element;
    }
    return null;
  };

  const handleLogout = () => {
     <Navigate to="/" />;

    localStorage.removeItem("token");
    localStorage.removeItem("name");
    localStorage.removeItem("isAdmin");
    localStorage.setItem("loggedIn", "false");
    setToken(null);
    window.location.reload()
  };

  const login = () => {
    const isLoggedIn = window.localStorage.token;
    if (isLoggedIn === "null") {
      return <NavLink className="Logout-LoginButton" to="/Login">Login/Signup</NavLink>
      ;
    } else {
      return <button className="Logout-LoginButton" onClick={() => handleLogout()}>Logout</button>;
    }
  };

  return (
    <nav>
      <NavLink to="/" className="logo"></NavLink>
      <div className="PageNavigator">
        <NavLink to="/">Home</NavLink>
        <NavLink to="/Search">Search</NavLink>
        <NavLink to="/Challenge">Challenge</NavLink>
        <NavLink to="/Profile">Profile</NavLink>
        {checkAdminAccess(<NavLink to="/Dashboard">Dashboard</NavLink>)}
      </div>

      {login()}
    </nav>
  );
}

export default NavigationBar;
