import { useState } from "react";
import './Login.css'
import useToken from './useToken';
import 'react-toastify/dist/ReactToastify.css';
import { NavLink } from 'react-router-dom';
import axios from 'axios'


function Signup() {
  const [isSignupMode, setIsSignupMode] = useState(false);
  const [email, setEmail] = useState('');
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const [profilePic, setImage] = useState(null);
  const { setToken } = useToken();
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const registerUser = async (userData) => {
    try {
      const formData = new FormData();
      formData.append('name', userData.name);
      formData.append('email', userData.email);
      formData.append('password', userData.password);
      formData.append('image', userData.image);

      const response = await axios.post('http://localhost:1112/user', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      localStorage.setItem('token', response.data.token);
      localStorage.setItem('name', response.data.name);
      localStorage.setItem('email', response.data.email);
      localStorage.setItem('password', response.data.password);
      localStorage.setItem('profilePic', response.data.profilePic);

      setIsLoggedIn(true);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleSignUp = async (e) => {
    e.preventDefault();
    const userData = {
      name,
      email,
      password,
      profilePic
    };
    registerUser(userData);
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onloadend = () => {
      setImage(reader.result);
    }
  }
  return (
    <div className="Login">
      <div className="Loginbox">
        <NavLink to="/" className="logo loginLogo"></NavLink>
        <h2 className="Logintitle">Sign up</h2>
        <form action="#" className="LoginForm">
          <div className="LoginInputs">
            <div className="input-field">
              <input type="file" name="Image" className="Logininput" onChange={handleImageChange} />
            </div>
            <div className="input-field">
              <input type="text" placeholder="Name" className="Logininput" onChange={(e) => setName(e.target.value)} />
            </div>
            <div className="input-field">
              <input type="text" placeholder="Email" className="Logininput" onChange={(e) => setEmail(e.target.value)} />
            </div>
            <div className="input-field">
              <input type="password" placeholder="Password" className="Logininput" onChange={(e) => setPassword(e.target.value)} />
            </div>
          </div>
          <div className="LoginButton-txt">
            <input type="submit" value="Login" className="btn solid LoginButton" onClick={handleSignUp} />
            <p>
              Create a new account
              <NavLink to="/Signup" className="signupInLoginTxt"> signup.</NavLink>
            </p>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Signup;
