import { useState } from "react";
import './Login.css'
import useToken from './useToken';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Navigate } from 'react-router';
import axios from 'axios'

async function loginUser(credentials) {
  return fetch('http://localhost:1112/user/login', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(credentials)
  })
    .then(response => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      return response.json();
    })
    .then(data => {
      localStorage.setItem('token', data.token);
      localStorage.setItem('isAdmin', data.isAdmin);
      return data;
    })
    .catch(error => {
      console.error('Error:', error);
      throw error;
    });
}




function Login() {
  const [isSignupMode, setIsSignupMode] = useState(false);
  const [email, setEmail] = useState('');
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const { setToken } = useToken();
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleSignupClick = () => {
    setIsSignupMode(true);
  };

  const handleSigninClick = () => {
    setIsSignupMode(false);
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const response = await loginUser({
        email,
        password,
        name
      });
      setToken(response.token);
      localStorage.setItem('name', response.name);
      localStorage.setItem('token', response.token);
      localStorage.setItem('isAdmin', response.isAdmin);
      localStorage.setItem('loggedIn', 'true');
      toast.success('Logged in successfully!');
      setIsLoggedIn(true);
    } catch (error) {
      console.error('Error:', error);
      toast.error('Wrong Username or Password!!');
    }
  }

  const registerUser = async (userData) => {
    try {
      const response = await axios.post('http://localhost:1112/user', userData);
      localStorage.setItem('token', response.data.token);
      localStorage.setItem('isAdmin', response.data.isAdmin);
      localStorage.setItem('loggedIn', 'true');
      toast.success('Registered and logged in successfully!');
      setIsLoggedIn(true);
    } catch (error) {
      console.error('Error:', error);
      toast.error('Registration failed!');
    }
  };

  const handleSignUp = async (e) => {
    e.preventDefault();
    const userData = {
      name,
      email,
      password,
      phoneNumber,
    };
    registerUser(userData);
  };


  if (isLoggedIn) {
    const isAdmin = localStorage.getItem('isAdmin') === 'true';
    if (isAdmin) {
      return <Navigate to="/dashboard" />;
    } else {
      return <Navigate to="/" />;
    }
  }

  return (
    <div className={`container ${isSignupMode ? "sign-up-mode" : ""}`}>
      <div className="forms-container">
        <div className="signin-signup">
          <form action="#" className="sign-in-form">
            <h2 className="title">Sign in</h2>
            <div className="input-field">
              <i className="fas fa-user"></i>
              <input type="text" placeholder="Email" onChange={(e) => setEmail(e.target?.value)} />
            </div>
            <div className="input-field">
              <i className="fas fa-lock"></i>
              <input type="password" placeholder="Password" onChange={(e) => setPassword(e.target?.value)} />
            </div>
            <input type="submit" value="Login" className="btn solid" onClick={handleSubmit} />
          </form>
          <form action="#" className="sign-up-form">
            <h2 className="title">Sign up</h2>
            <div className="input-field">
              <i className="fas fa-user"></i>
              <input type="text" placeholder="Name" onChange={(e) => setName(e.target?.value)} />
            </div>
            <div className="input-field">
              <i className="fas fa-envelope"></i>
              <input type="email" placeholder="Email" onChange={(e) => setEmail(e.target?.value)} />
            </div>
            <div className="input-field">
              <i className="fas fa-lock"></i>
              <input type="password" placeholder="Password" onChange={(e) => setPassword(e.target?.value)} />
            </div>

            <div className="input-field">
              <i className="fas fa-lock"></i>
              <input type="number" placeholder="Phone Number" onChange={(e) => setPhoneNumber(e.target?.value)} />
            </div>
            <input type="submit" className="btn" value="Sign up" onClick={handleSignUp} />
          </form>
        </div>
      </div>
      <div className="panels-container">
        <div className="panel left-panel">
          <div className="content">
            <h3>New here ?</h3>
            <p>
              Lorem ipsum, dolor sit amet consectetur adipisicing elit.
              Debitis, ex ratione. Aliquid!
            </p>
            <button className="btn transparent" onClick={handleSignupClick}>
              Sign up
            </button>
          </div>
          <img src="img/log.svg" className="image" alt="" />
        </div>
        <div className="panel right-panel">
          <div className="content">
            <h3>One of us ?</h3>
            <p>
              Lorem ipsum dolor sit amet consectetur adipisicing elit. Nostrum
              laboriosam ad deleniti.
            </p>
            <button className="btn transparent" onClick={handleSigninClick}>
              Sign in
            </button>
          </div>
          <img src="img/register.svg" className="image" alt="" />
        </div>
      </div>
    </div>
  );


}
export default Login;
