import { useState, useEffect } from "react";
import axios from "axios";

function CreateRecipe() {
  const [categories, setCategories] = useState([]);

  const [formData, setFormData] = useState({
    title: "",
    description: "",
    ingredients: "",
    steps: "",
    category: "",
    image: "",
  });

  useEffect(() => {
    async function fetchCategories() {
      const userId = localStorage.getItem("id");
      const formDataToSend = new FormData();
      formDataToSend.append("title", formData.title);
      formDataToSend.append("description", formData.description);
      formDataToSend.append("ingredients", formData.ingredients);
      formDataToSend.append("steps", formData.steps);
      formDataToSend.append("category", formData.category);
      formDataToSend.append("image", formData.image);

      try {
        const res = await axios.post(
          `http://localhost:1112/resipe/${userId}`,
          formDataToSend,
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          }
        );
      } catch (err) {
        console.error(err);
      }
    }
    fetchCategories();
  }, []);

  // fetching categories for the option input
  useEffect(() => {
    async function fetchCategories() {
      try {
        const res = await axios.get("http://localhost:1112/category");
        setCategories(res.data.categories);
      } catch (err) {
        console.error(err);
      }
    }
    fetchCategories();
  }, []);

  return (
    <section>
      <form>
        <div className="input-add-rent">
          <p>{formData._id ? "Update" : "Add"} an Item</p>
          <input
            className="name-rent-form"
            type="text"
            name="name"
            placeholder="Name"
            onChange={handleChange}
            value={formData.name}
            required
          />
          <input
            className="description-rent-form"
            type="text"
            name="description"
            placeholder="Description"
            onChange={handleChange}
            value={formData.description}
            required
          />
          <input
            className="quantity-rent-form"
            type="text"
            name="quantity"
            placeholder="Quantity"
            onChange={handleChange}
            value={formData.quantity}
            required
          />
          <input
            className="price-rent-form"
            type="text"
            name="price"
            placeholder="Price"
            onChange={handleChange}
            value={formData.price}
            required
          />
          <input
            className="image-rent-form"
            type="file"
            accept="image/*"
            name="image"
            id="file-input"
            onChange={handleChange}
            required
          />
          <select
            className="my-select-currency"
            name="currency"
            onChange={handleChange}
            value={formData.currency}
            required
          >
            <option value="">Select a Currency</option>
            <option value="L.L.">L.L.</option>
            <option value="USD">USD</option>
          </select>
          <button className="btn-add-Rent" type="submit">
            Create Recipe
          </button>
        </div>
      </form>
    </section>
  );
}
export default CreateRecipe;
