import { useState } from "react";
import './Login.css'
import useToken from './useToken';
import 'react-toastify/dist/ReactToastify.css';
import { NavLink } from 'react-router-dom';
import axios from 'axios'


function Signup() {
  const [isSignupMode, setIsSignupMode] = useState(false);
  const [email, setEmail] = useState('');
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const { setToken } = useToken();
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // const handleSignupClick = () => {
  //   setIsSignupMode(true);
  // };


  const registerUser = async (userData) => {
    try {
      const response = await axios.post('http://localhost:1112/user', userData);
      localStorage.setItem('token', response.data.token);
      localStorage.setName('name', response.data.name);
      localStorage.setEmail('email', response.data.email);
      localStorage.setItem('password', response.data.password);

      setIsLoggedIn(true);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleSignUp = async (e) => {
    e.preventDefault();
    const userData = {
      name,
      email,
      password,
    };
    registerUser(userData);
  };




  return (
    <div className="Login">
    <div className="Loginbox">
      <NavLink to="/" className="logo loginLogo"></NavLink>
      <h2 className="Logintitle">Sign up</h2>
      {/* {valid ? <i className="ErrorMessageLogin">Wrong password or email</i> : null  } */}
      <form action="#" className="LoginForm">
        <div className="LoginInputs">
        <div className="input-field">

            <input type="text" placeholder="Name" className="Logininput" onChange={(e) => setName(e.target?.value)}
            />
          </div>
          <div className="input-field">
            <input
              type="text" placeholder="Email" className="Logininput" onChange={(e) => setEmail(e.target?.value)} />
          </div>
          <div className="input-field">
            <input type="password"placeholder="Password" className="Logininput" onChange={(e) => setPassword(e.target?.value)}
            />
          </div>

        </div>
        <div className="LoginButton-txt">
          <input type="submit" value="Login" className="btn solid LoginButton" onClick={handleSignUp} />
          <p>
            Create a new account
            <NavLink to="/Signup" className="signupInLoginTxt"> signup.</NavLink>
          </p>
        </div>
      </form>
    </div>
  </div>
  );


}
export default Signup;
