import { useState , useEffect} from "react";
import "./Login.css";
import useToken from "./useToken";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { NavLink } from "react-router-dom";



function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [valid, setValid] = useState(false);
  const { setToken } = useToken();
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  async function loginUser(credentials) {
    return fetch("http://localhost:1112/user/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(credentials),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        localStorage.setItem("token", data.token);
        localStorage.setItem("isAdmin", data.isAdmin);
        return data;
      })
      .catch((error) => {
        console.error("Error:", error);
        throw error;
      });
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await loginUser({
        email,
        password,
      });

      setToken(response.token);
      localStorage.setItem("token", response.token);
      localStorage.setItem("isAdmin", response.isAdmin);
      localStorage.setItem("loggedIn", "true");
      toast.success("Logged in successfully!");
      setIsLoggedIn(true);
    } catch (error) {
      // console.error("Error:", error);
      setValid(true);
      toast.error("Wrong email or Password!!");
    }
  };

  if (isLoggedIn) {
    const isAdmin = localStorage.getItem("isAdmin") === "true";
    if (isAdmin) {
      window.location.href = "/Dashboard";
    } else {
      window.location.href = "/";
    }
  }
  useEffect(() => {
    let timer;
    if (valid) {
      timer = setTimeout(() => {
        setValid(false);
      }, 4000);
    }
    return () => clearTimeout(timer);
  }, [valid]);
  return (
    <div className="Login">
      <div className="Loginbox">
        <NavLink to="/" className="logo loginLogo"></NavLink>

        <h2 className="Logintitle">Login</h2>

        {valid ? <i className="ErrorMessageLogin">Wrong password or email</i> : null  }
        {/* <ToastContainer /> */}
        <form action="#" className="LoginForm">
          <div className="LoginInputs">
            <div className="input-field">
              <input
                type="text"
                placeholder="Email"
                required
                className="Logininput"
                onChange={(e) => setEmail(e.target?.value)}
              />
            </div>
            <div className="input-field">
              <input
                type="password"
                placeholder="Password"
                required
                className="Logininput"
                onChange={(e) => setPassword(e.target?.value)}
              />
            </div>
          </div>
          <div className="LoginButton-txt">
            <input
              type="submit"
              value="Login"
              className="btn solid LoginButton"
              onClick={handleSubmit}
            />
            <p>
              Create a new account
              <NavLink to="/Signup" className="signupInLoginTxt">
                {" "}
                signup.
              </NavLink>
            </p>
          </div>
        </form>
      </div>
    </div>
  );
}
export default Login;
