import { useState, useEffect } from "react";
import axios from "axios";

function CreateRecipe() {
  const [categories, setCategories] = useState([]);

  const [formData, setFormData] = useState({
    title: "",
    description: "",
    ingredients: "",
    steps: "",
    category: "",
    image: "",
  });

  useEffect(() => {
    async function fetchCategories() {
      const userId = localStorage.getItem("id");
      const formDataToSend = new FormData();
      formDataToSend.append("name", formData.title);
      formDataToSend.append("description", formData.description);
      formDataToSend.append("quantity", formData.ingredients);
      formDataToSend.append("price", formData.price);
      formDataToSend.append("currency", formData.currency);
      formDataToSend.append("image", formData.image);

      try {
        const res = await axios.post(
          `http://localhost:1112/resipe/${userId}`,
          formData,
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          }
        );
        setCategories(res.data.categories);
      } catch (err) {
        console.error(err);
      }
    }
    fetchCategories();
  }, []);

  // fetching categories for the option input
  useEffect(() => {
    async function fetchCategories() {
      try {
        const res = await axios.get("http://localhost:1112/category");
        setCategories(res.data.categories);
      } catch (err) {
        console.error(err);
      }
    }
    fetchCategories();
  }, []);

  return <div>CreateRecipe</div>;
}
export default CreateRecipe;
