import "./Home.css";
import { CSSTransition } from "react-transition-group";
import { useState, useEffect } from "react";
import axios from "axios";
import banner from "../../../assets/banner-flag-bookmark-svgrepo-com.svg";

function Home() {
  const [data, setData] = useState([]);
  const [showPopup, setShowPopup] = useState(false);
  const [cardData, setCardData] = useState([
    {
      image: "",
      title: "",
      description: "",
      ingredients: [],
      steps: "",
    },
  ]);

  useEffect(() => {
    const fetchData = async () => {
      const response = await axios.get(`http://localhost:1112/recipe`);

      const userData = await Promise.all(
        response.data.map((item) =>
          axios.get(`http://localhost:1112/user/${item.user}`)
        )
      );
      const combinedData = response.data.map((recipe, index) => ({
        ...recipe,
        userimg: userData[index].data.profilePic,
        username:userData[index].data,
        liked: false,
      }));


      combinedData.sort(
        (a, b) => new Date(b.createdAt) - new Date(a.createdAt)
      );

      setData(combinedData);
    };

    fetchData();
  }, []);

  const handleImageClick = (index) => {
    setShowPopup(true);
    setCardData(data[index]);
  };
  const handleClosePopup = () => {
    setShowPopup(false);
  };

  return (
    <section className="HomePage">
      <div className="HomeMainSlider">
        {data.map((item, index) => (
          <div key={index} className="HomeCard">
            <header className="HomeCardHeader">
              <div className="HomeCardHeaderTitleRating">
                <img
                  src={item.userimg}
                  alt="profilepic"
                  className="HomeCardHeaderPic"
                />
                <h1 className="HomeCardHeaderTitle">{item.name}</h1>

                <p> Raiting</p>
              </div>

              <img src={banner} alt="save" className="HomeCardSave" />
            </header>
            <div className="HomeCardMainImage">
              <img
                src={item.image}
                alt="main Recipe"
                className="HomeCardMainImage"
                onClick={() => handleImageClick(index)}
              />
            </div>

            <div className="HomeCardDescription">
              <h2>Description: </h2>
              <p>{item.description}</p>
            </div>
          </div>
        ))}
      </div>

      <CSSTransition
        in={showPopup}
        timeout={300}
        classNames="Popup"
        unmountOnExit
      >
        <div className="Popup">
          <div className="RecipeCArd">
            <img src={cardData.image} alt="Card" />
            <div className="RecipeCArdContent">
              <h1>{cardData.title}:</h1>
              <p>{cardData.description}</p>
              <h1>Ingredients:</h1>

              {cardData.ingredients &&
                cardData.ingredients.map((ingredient, index) => (
                  <li key={index}>{ingredient}</li>
                ))}

              <h1>How to apply the recipe:</h1>
              <p>{cardData.steps}</p>
              <h2 onClick={handleClosePopup} className="CardCloseButton">
                close
              </h2>
            </div>
          </div>
        </div>
      </CSSTransition>
    </section>
  );
}

export default Home;
