import { useState } from "react";
import "./Login.css";
// import useToken from "./useToken";
import "react-toastify/dist/ReactToastify.css";
import { NavLink } from "react-router-dom";
import axios from "axios";

function Signup() {
  // const { setToken } = useToken();
  const [user, setUser] = useState([]);
  const [formData, setFormData] = useState({
    _id: "",
    name: "",
    email: "",
    password: "",
    image: "",
  });
  const registerUser = async (event) => {
    event.preventDefault();
    const formData = new FormData();
    formData.append("image", event.target.files[0]);

    try {
      const config = {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      };
      const res = await axios.post(
        "http://localhost:5000/slideshow",
        formData,
        config
      );
      set([...slides, res.data.data]);
    } catch (err) {
      console.error(err);
    }
  };
  // const registerUser = async (userData) => {
  //   try {
  //     const formDataToSend = new FormData();
  //     formDataToSend.append("name", userData.name);
  //     formDataToSend.append("email", userData.email);
  //     formDataToSend.append("password", userData.password);
  //     formDataToSend.append("image", userData.image);

  //     const res = await axios.post(
  //       "http://localhost:1112/user",
  //       formDataToSend
  //     );
  //     setUser((prevRents) => [...prevRents, res.data.data]);
  //   } catch (err) {
  //     console.log(err);
  //   }
  // };

  const handleSignUp = async (e) => {
    e.preventDefault();
    registerUser(formData);
  };

  // const handleImageChange = (e) => {
  //   const file = e.target.files[0];
  //   const reader = new FileReader();
  //   reader.readAsDataURL(file);
  //   reader.onloadend = () => {
  //     setFormData({ ...formData, image: reader.result });
  //   };
  // };

  return (
    <div className="Login">
      <div className="Loginbox">
        <NavLink to="/" className="logo loginLogo"></NavLink>
        <h2 className="Logintitle">Sign up</h2>
        <form action="#" className="LoginForm">
          <div className="LoginInputs">
            <div className="input-field">
              <input
                type="file"
                name="Image"
                className="Logininput"
                onChange={handleImageChange}
              />
            </div>
            <div className="input-field">
              <input
                type="text"
                placeholder="Name"
                className="Logininput"
                onChange={(e) =>
                  setFormData({ ...formData, name: e.target.value })
                }
              />
            </div>
            <div className="input-field">
              <input
                type="text"
                placeholder="Email"
                className="Logininput"
                onChange={(e) =>
                  setFormData({ ...formData, email: e.target.value })
                }
              />
            </div>
            <div className="input-field">
              <input
                type="password"
                placeholder="Password"
                className="Logininput"
                onChange={(e) =>
                  setFormData({ ...formData, password: e.target.value })
                }
              />
            </div>
          </div>
          <div className="LoginButton-txt">
            <input
              type="submit"
              value="Sign Up"
              className="btn solid LoginButton"
              onClick={handleSignUp}
            />
            <p>
              Already have an account?
              <NavLink to="/" className="signupInLoginTxt">
                {" "}
                Login.
              </NavLink>
            </p>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Signup;
