import { useState } from "react";
import Login from "./Login";
import Signup from "./Signp";

function LoginSignupCard() {
  const [showLogin, setShowLogin] = useState(true);

  const handleToggle = () => {
    setShowLogin(!showLogin);
  };

  return (
    <div className="LoginSignupCard">
      <div className={`login-signup-card ${showLogin ? 'show-login' : 'show-signup'}`}>
        <Login />
        <Signup />
      </div>
      <button onClick={handleToggle}>{showLogin ? 'Sign up' : 'Log in'}</button>
    </div>
  );
}

export default LoginSignupCard;
