import "./Home.css";
import { CSSTransition } from "react-transition-group";
import { useState, useEffect } from "react";
import axios from "axios";
import banner from "../../../assets/banner-flag-bookmark-svgrepo-com.svg";
import ReactCSSTransitionGroup from 'react-addons-css-transition-group';

function Home() {
  const [data, setData] = useState([]);
  const [showPopup, setShowPopup] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      const response = await axios.get(`http://localhost:1112/recipe`);

      const userData = await Promise.all(
        response.data.map((item) =>
          axios.get(`http://localhost:1112/user/${item.user}`)
        )
      );

      const combinedData = response.data.map((recipe, index) => ({
        ...recipe,
        user: userData[index].data.profilePic,
        liked: false,
      }));

      combinedData.sort(
        (a, b) => new Date(b.createdAt) - new Date(a.createdAt)
      );

      setData(combinedData);
    };

    fetchData();
  }, []);

  const handleImageClick = () => {
    setShowPopup(true);
  };
  const handleClosePopup = () => {
    setShowPopup(false);
  };

  return (
    <section className="HomePage">
      <div className="HomeMainSlider">
        <ReactCSSTransitionGroup
          transitionName="card"
          transitionAppear={true}
          transitionAppearTimeout={500}
          transitionEnterTimeout={500}
          transitionLeaveTimeout={300}>
          {data.map((item, index) => (
            <div key={index} className="HomeCard">
              <header className="HomeCardHeader">
                <div>
                  <img
                    src={item.user}
                    alt="profilepic"
                    className="HomeCardHeaderPic"
                  />
                  <div className="HomeCardHeaderTitleRating">
                    <h1 className="HomeCardHeaderTitle">{item.title}</h1>

                    <p> Raiting</p>
                  </div>
                </div>
                <img src={banner} alt="save" className="HomeCardSave" />
              </header>
              <div className="HomeCardMainImage">
                <img
                  src={item.image}
                  alt="main Recipe"
                  className="HomeCardMainImage"
                  onClick={handleImageClick}
                />
              </div>

              <div className="HomeCardDescription">
                <h2>Description: </h2>
                <p>{item.description}</p>
              </div>
            </div>
          ))}
        </ReactCSSTransitionGroup>
      </div>

      <CSSTransition
        in={showPopup}
        timeout={300}
        classNames="Popup"
        unmountOnExit
      >
        <div className="Popup">
          <dic className="RecipeCArd"></dic>
          <h2 onClick={handleClosePopup}>close</h2>
        </div>
      </CSSTransition>
    </section>
  );
}

export default Home;
