import { useState } from "react";
import "./Login.css";
import useToken from "./useToken";
import "react-toastify/dist/ReactToastify.css";
import { NavLink } from "react-router-dom";
import axios from "axios";

function Signup() {
  const { setToken } = useToken();
  const [user, setUser] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    image: "",
  });

  const registerUser = async (userData) => {
    try {
      const res = await axios.post(
        "http://localhost:1112/user",
        userData
      );
      setUser((prevUsers) => [...prevUsers, res.data.data]);
    } catch (err) {
      console.error(err);
    }
  };

  const handleSignUp = async (e) => {
    e.preventDefault();
    registerUser(formData);
  };

  return (
    <div className="Login">
      <div className="Loginbox">
        <NavLink to="/" className="logo loginLogo"></NavLink>
        <h2 className="Logintitle">Sign up</h2>
        <form action="#" className="LoginForm">
          <div className="LoginInputs">
            <div className="input-field">
              <input
                type="file"
                name="Image"
                className="Logininput"
                onChange={(e) =>
                  setFormData({ ...formData, image: e.target.files[0] })
                }
              />
            </div>
            <div className="input-field">
              <input
                type="text"
                placeholder="Name"
                className="Logininput"
                onChange={(e) =>
                  setFormData({ ...formData, name: e.target.value })
                }
              />
            </div>
            <div className="input-field">
              <input
                type="text"
                placeholder="Email"
                className="Logininput"
                onChange={(e) =>
                  setFormData({ ...formData, email: e.target.value })
                }
              />
            </div>
            <div className="input-field">
              <input
                type="password"
                placeholder="Password"
                className="Logininput"
                onChange={(e) =>
                  setFormData({ ...formData, password: e.target.value })
                }
              />
            </div>
          </div>
          <div className="LoginButton-txt">
            <input
              type="submit"
              value="Sign Up"
              className="btn solid LoginButton"
              onClick={handleSignUp}
            />
            <p>
              Already have an account?
              <NavLink to="/" className="signupInLoginTxt">
                {" "}
                Login.
              </NavLink>
            </p>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Signup;
