import { useState, useEffect } from "react";
import axios from "axios";
import "./RecipeCreate.css";
import { useNavigate } from "react-router-dom";
function CreateRecipe() {
  const [categories, setCategories] = useState([]);
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    ingredients: "",
    steps: "",
    category: "",
    image: "",
  });
  const navigate = useNavigate();
  const [isSubmitting, setIsSubmitting] = useState(false); // new state variable

  const PostRecipe = async () => {
    const userId = sessionStorage.getItem("id");
    const formDataToSend = new FormData();
    formDataToSend.append("title", formData.title);
    formDataToSend.append("description", formData.description);
    formDataToSend.append("ingredients", formData.ingredients);
    formDataToSend.append("steps", formData.steps);
    formDataToSend.append("category", formData.category);
    formDataToSend.append("image", formData.image);

    try {
      await axios.post(
        `http://localhost:1112/recipe/${userId}`,
        formDataToSend,
        {
          headers: {
            Authorization: `Bearer ${sessionStorage.getItem("token")}`,
          },
        }
      );
      setFormData({
        title: "",
        description: "",
        ingredients: "",
        steps: "",
        category: "",
        image: "",
      });
    } catch (err) {
      console.error(err);
      // handle error here
    } finally { // reset isSubmitting flag after the function completes
      setIsSubmitting(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setIsSubmitting(true); // set isSubmitting to true when form is submitted
    PostRecipe();
  };

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const res = await axios.get("http://localhost:1112/category");
        const sortedCategories = res.data.categories.sort((a, b) =>
          a.title.localeCompare(b.title)
        );
        setCategories(sortedCategories);
      } catch (err) {
        console.error(err);
      }
    };
    fetchCategories();
  }, []);

  const handleChange = (e) => {
    const name = e.target.name;
    const value =
      e.target.type === "file" ? e.target.files?.[0] : e.target.value;

    if (name === "category") {
      const selectedIndex = e.target.selectedIndex - 1;
      const selectedOption = categories[selectedIndex];

      if (selectedOption) {
        setFormData((prevData) => ({
          ...prevData,
          [name]: selectedOption._id,
        }));
      }
    } else {
      setFormData((prevData) => ({
        ...prevData,
        [name]: value,
      }));
    }
  };

  return (
    <section className="RecipeCreateSection">
      <form onSubmit={handleSubmit}>
        <h1>Post a Recipe</h1>
        <label htmlFor="title">Title:</label>
        <input
          type="text"
          id="title"
          name="title"
          placeholder="Title"
          onChange={handleChange}
          value={formData.title}
          required
          maxLength="25"
        />
        <label htmlFor="description">Description:</label>
        <input
          type="text"
          id="description"
          name="description"
          placeholder="Description"
          onChange={handleChange}
          value={formData.description}
          maxLength="250"
          required
          spellCheck
        />
        <label htmlFor="ingredients">Ingredients:</label>
        <input
          type="text"
          id="ingredients"
          name="ingredients"
          placeholder="Ingredients"
          onChange={handleChange}
          value={formData.ingredients}
          required
          spellCheck
        />
        <label htmlFor="steps">Steps:</label>
        <input
          type="text"
          id="steps"
          name="steps"
          placeholder="Steps"
          onChange={handleChange}
          value={formData.steps}
          required
          spellCheck
        />
        <label htmlFor="image">Image:</label>
        <input
          type="file"
          accept="image/*"
          id="image"
          name="image"
          onChange={handleChange}
          required
        />

        <label htmlFor="category">Category:</label>
        <select
          id="category"
          name="category"
          onChange={handleChange}
          value={formData.category}
          required
        >
          <option value=""> Choose a Category</option>
          {categories.map((category) => (
            <option key={category._id} value={category._id}>
              {category.title}
            </option>
          ))}
        </select>
        <button className="btn-add-recipe" type="submit" disabled={isSubmitting}> {/* disable submit button when form is submitting */}
          {isSubmitting ? "Creating Recipe..." : "Create Recipe"} {/* change button text when form is submitting */}
        </button>
      </form>
      <div className="DemoRecipe">
        {/* demo recipe code */}
      </div>
    </section>
    );
    }

    export default CreateRecipe;
