import { useState, useEffect } from "react";
import axios from "axios";
import "./RecipeCreate.css";
function CreateRecipe() {
  const [categories, setCategories] = useState([]);

  const [formData, setFormData] = useState({
    title: "",
    description: "",
    ingredients: "",
    steps: "",
    category: "",
    image: "",
  });

  async function PostRecipe() {
    const userId = localStorage.getItem("id");
    const formDataToSend = new FormData();
    formDataToSend.append("title", formData.title);
    formDataToSend.append("description", formData.description);
    formDataToSend.append("ingredients", formData.ingredients);
    formDataToSend.append("steps", formData.steps);
    formDataToSend.append("category", formData.category);
    formDataToSend.append("image", formData.image);

    try {
      const res = await axios.post(
        `http://localhost:1112/resipe/userId}`,
        formDataToSend,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );
    } catch (err) {
      console.error(err);
    }
  }
  const handleSubmit = async (e) => {
    e.preventDefault();
    await PostRecipe();
  };

  // fetching categories for the option input
  useEffect(() => {
    async function fetchCategories() {
      try {
        const res = await axios.get("http://localhost:1112/category");
        setCategories(res.data.categories);
      } catch (err) {
        console.error(err);
      }
    }
    fetchCategories();
  }, []);

  const handleChange = (e, value) => {
    const name = e.target.name;
    value = e.target.type === "file" ? e.target.files[0] : e.target.value;

    if (name === "category") {
      const selectedIndex = e.target.selectedIndex;
      const selectedOption = categories[selectedIndex];
      setFormData((prevData) => ({
        ...prevData,
        [name]: selectedOption.title,
      }));
    } else {
      setFormData((prevData) => ({
        ...prevData,
        [name]: value,
      }));
    }
  };

  return (
    <section className="RecipeCreateSection">
      <form onSubmit={handleSubmit}>
        <p>Create a Recipe</p>
        <input
          className="title-recipe-form"
          type="text"
          name="title"
          placeholder="Title"
          onChange={handleChange}
          value={formData.title}
          required
        />
        <input
          className="description-recipe-form"
          type="text"
          name="description"
          placeholder="Description"
          onChange={handleChange}
          value={formData.description}
          required
        />
        <input
          className="ingredients-recipe-form"
          type="text"
          name="ingredients"
          placeholder="ingredients"
          onChange={handleChange}
          value={formData.ingredients}
          required
        />
        <input
          className="steps-recipe-form"
          type="text"
          name="steps"
          placeholder="steps"
          onChange={handleChange}
          value={formData.steps}
          required
        />
        <input
          className="image-recipe-form"
          type="file"
          accept="image/*"
          name="image"
          id="file-input"
          onChange={handleChange}
          required
        />
        <select
          className="my-select-category"
          name="category"
          onChange={handleChange}
          value={formData.category}
          required
        >
          {categories.map((category) => (
            <option key={category._id} value={category._id}>
              {category.title}
            </option>
          ))}
        </select>
        <button className="btn-add-recipe" type="submit">
          Create Recipe
        </button>
      </form>
    </section>
  );
}
export default CreateRecipe;
