import { NavLink, Route, Routes } from "react-router-dom";
import "./Navbar.css";
import logo from "../../assets/cookieLogo.png";
import useToken from "./useToken";
import { Navigate } from "react-router-dom";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function NavigationBar() {
  const { setToken } = useToken();

  const isAdmin = localStorage.getItem("isAdmin") === "true";
  const handleLogout = () => {
    window.location.href = "/";
    localStorage.removeItem("token");
    localStorage.removeItem("name");
    localStorage.removeItem("isAdmin");
    localStorage.setItem("loggedIn", "false");
    setToken(null);
  };

  const isLoggedIn = window.localStorage.token;

  const login = () => {
    if (isLoggedIn === "null") {
      return (
        <>
          <NavLink className="Logout-LoginButton" to="/Login">
            Login/Signup
          </NavLink>
        </>
      );
    } else {
      return (
        <div className="Logout-LoginButton Logout-LoginButtonLogout" onClick={() => handleLogout()}>
          Logout
        </div>
      );
    }
  };

  const checkAdminAccess = (element) => {
    if (isAdmin) {
      return element;
    } else {
      const pathName = window.location.pathname;
      const isDashboardPage = pathName.startsWith("/dashboard");
      const isDashboardErrorActive = toast.isActive("dashboard-error");

      if (isDashboardPage && !isDashboardErrorActive) {
        toast.error("You don't have access to this part of the website");
      }

      return <Navigate to="/dashboard" replace />;
    }
  };

  return (
    <nav>
      <NavLink to="/">
        <img src={logo} alt="" className="logo" />
      </NavLink>
      <div className="PageNavigator">
        <NavLink to="/">Home</NavLink>
        <NavLink to="/Search">Search</NavLink>
        <NavLink to="/Challenge">Challenge</NavLink>
        {isLoggedIn !== "null" ? (
          <NavLink to="/Profile">Profile</NavLink>
        ) : null}
        {isAdmin ? <NavLink to="/Dashboard">Dashboard</NavLink> : null}
      </div>

      {login()}
    </nav>
  );
}

function Dashboard() {
  return <h1>dash</h1>;
}

export default NavigationBar;
