import { NavLink} from "react-router-dom";
import "./Navbar.css";
import logo from "../../assets/cookieLogo.png";
import { Navigate } from "react-router-dom";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Dashboard from "../dashboard/Dashboard";
function NavigationBar() {
  const isAdmin = localStorage.getItem("isAdmin") === "true";
  const handleLogout = () => {
    window.location.href = "/Login";
    localStorage.removeItem("token");
    localStorage.removeItem("name");
    localStorage.removeItem("isAdmin");
    localStorage.removeItem("id");
    localStorage.clear();
  };

  const isLoggedIn = window.localStorage.token;

  const login = () => {
    if (!isLoggedIn) {
      return (
        <>
          <NavLink className="Logout-LoginButton" to="/Login">
            Login/Signup
          </NavLink>
        </>
      );
    } else {
      return (
        <div
          className="Logout-LoginButton Logout-LoginButtonLogout"
          onClick={() => handleLogout()}
        >
          Logout
        </div>
      );
    }
  };

  const checkAdminAccess = (element) => {
    if (isAdmin) {
      return element;
    } else {
      const pathName = window.location.pathname;
      const isDashboardPage = pathName.startsWith("/dashboard");
      const isDashboardErrorActive = toast.isActive("dashboard-error");

      if (isDashboardPage && !isDashboardErrorActive) {
        toast.error("You don't have access to this part of the website");
      }

      return <Navigate to="/dashboard" replace />;
    }
  };

  return (
    <>
      <nav className="NavigatorBar">
        <NavLink to="/" className="Navlogo">
          <img src={logo} alt="" className="Navlogo" />
        </NavLink>
        <div className="PageNavigator ">
          <NavLink to="/">Home</NavLink>
          <NavLink to="/search" style={{ display: "none" }}>
            Search
          </NavLink>
          <NavLink to="/challenge" style={{ display: "none" }}>
            Challenge
          </NavLink>
          <div className="LoginNavPhone Logout-LoginButtonLogout">
            {login()}
          </div>

          {isLoggedIn ? <NavLink to="/profile">Profile</NavLink> : null}
          {isAdmin ? <NavLink to="/dashboard">Dashboard</NavLink> : null}
        </div>

        <div className="login-LoginButtonLogout">{login()}</div>
      </nav>

      <ToastContainer />
    </>
  );
}

export default NavigationBar;
