import { useState } from "react";
import "./Login.css";
import useToken from "./useToken";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { Navigate } from "react-router";
import { NavLink } from "react-router-dom";

async function loginUser(credentials) {
  return fetch("http://localhost:1112/user/login", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(credentials),
  })
    .then((response) => {
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      return response.json();
    })
    .then((data) => {
      localStorage.setItem("token", data.token);
      localStorage.setItem("isAdmin", data.isAdmin);
      return data;
    })
    .catch((error) => {
      console.error("Error:", error);
      throw error;
    });
}

function Login() {
  const [email, setEmail] = useState("");
  const [name] = useState("");
  const [password, setPassword] = useState("");
  const { setToken } = useToken();
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await loginUser({
        email,
        password,
        name,
      });
      setToken(response.token);
      localStorage.setItem("name", response.name);
      localStorage.setItem("token", response.token);
      localStorage.setItem("isAdmin", response.isAdmin);
      localStorage.setItem("loggedIn", "true");
      toast.success("Logged in successfully!");
      setIsLoggedIn(true);
    } catch (error) {
      console.error("Error:", error);
      toast.error("Wrong Username or Password!!");
    }
  };

  if (isLoggedIn) {
    const isAdmin = localStorage.getItem("isAdmin") === "true";
    if (isAdmin) {
      return <Navigate to="/dashboard" />;
    } else {
      return <Navigate to="/" />;
    }
  }

  return (
    <div className="Login">
      <div>

        </div>
          <NavLink to="/" className="logo loginLogo"></NavLink>

      <h2 className="title">Login in</h2>

      <form action="#" className="LoginForm">
        <div className="LoginInputs">
          <div className="input-field">
            <input type="text" placeholder="Email" className="Logininput"onChange={(e) => setEmail(e.target?.value)}/>
          </div>
          <div className="input-field">
            <input type="password" placeholder="Password" className="Logininput"onChange={(e) => setPassword(e.target?.value)}/>
          </div>
        </div>

        <div className="LoginButton-txt">
          <input
            type="submit"
            value="Login"
            className="btn solid LoginButton"
            onClick={handleSubmit}
          />
          <p>
            Create a new account
            <NavLink to="/Signup" className="signupInLoginTxt"> signup.</NavLink>
          </p>
        </div>
      </form>
    </div>
  );
}
export default Login;
