import axios from "axios";
import {useState, useEffect} from "react"
import  './MyRecipe.css'
function MyRecipes( ) {
    const [data, setData] = useState([]);
  const userId = localStorage.getItem("id")
    useEffect(() => {
        const fetchData = async () => {
            const response = await axios.get(`http://localhost:1112/recipe`);

            await Promise.all(
              response.data.map((item) =>
                axios.get(`http://localhost:1112/user/${item.user}`)
              )
            );

            const userId = localStorage.getItem("id");
            const userRecipes = response.data.filter((item) => item.user === userId);

            setData(userRecipes);
          };



      fetchData();
    }, [userId]);

    return (
      <div className="MyRecipeProfileSectiom">
         {data.map((item, index) => (
          <div key={index} className="MyRecipeCard">
            <p className="MyRecipeCardTitle">{item.title}</p>
              <img
                src={item.image}
                alt="main Recipe"
                className="MyRecipeCardImage"
                onClick={() => handleImageClick(index)}
              />
            </div>
        ))}
      </div>
    );
  }

  export default MyRecipes;
