import "./Home.css";
import { CSSTransition } from "react-transition-group";
import { useState, useEffect } from "react";
import axios from "axios";
import banner from "../../../assets/banner-flag-bookmark-svgrepo-com.svg";

function Home() {
  const [data, setData] = useState([]);
  const [showPopup, setShowPopup] = useState(false);
  const [showUserPopup, setShowUserPopup] = useState(false);
  const [cardData, setCardData] = useState({
    image: "",
    title: "",
    description: "",
    ingredients: [],
    steps: "",
    user: "",
    userimg: "",
    username: "",
    liked: false,
  });
  const [userRecipes, setUserRecipes] = useState([]);
  const [isFollowing, setIsFollowing] = useState(false); // New state for follow status

  useEffect(() => {
    const fetchData = async () => {
      const response = await axios.get(`http://localhost:1112/recipe`);

      const userData = await Promise.all(
        response.data.map((item) =>
          axios.get(`http://localhost:1112/user/${item.user}`)
        )
      );

      const combinedData = response.data.map((recipe, index) => ({
        ...recipe,
        userimg: userData[index].data.profilePic,
        username: userData[index].data.name,
        liked: false,
      }));

      combinedData.sort(
        (a, b) => new Date(b.createdAt) - new Date(a.createdAt)
      );

      setData(combinedData);
    };

    fetchData();
  }, []);

  const handleImageClick = (index) => {
    setShowPopup(true);
    setCardData(data[index]);
  };

  const handleClosePopup = () => {
    setShowPopup(false);
  };

  const handleUserImageClick = async (index) => {
    setShowUserPopup(true);
    setCardData(data[index]);

    const userId = data[index].user;
    const response = await axios.get(`http://localhost:1112/recipe/`);
    const userRecipes = response.data.filter((item) => item.user === userId);

    setUserRecipes(userRecipes);

    // Fetch follow status
    const followResponse = await axios.get(`http://localhost:1112/user/follow`);
    const loggedInUserId = "<LOGGED_IN_USER_ID>"; // Replace with the actual logged-in user ID
    const isFollowing = followResponse.data.some(
      (follow) =>
        follow.follower === loggedInUserId && follow.following === userId
    );
    setIsFollowing(isFollowing);
  };

  const toggleFollow = async () => {
    const userId = cardData.user;
    const loggedInUserId = "<LOGGED_IN_USER_ID>"; // Replace with the actual logged-in user ID

    if (isFollowing) {
      // Unfollow
      await axios.delete(`http://localhost:1112/user/unfollow`, {
        data: { follower: loggedInUserId, following: userId },
      });
    } else {
      // Follow
      await axios.post(`http://localhost:1112/user/follow`, {
        follower: loggedInUserId,
        following: userId,
      });
    }

    setIsFollowing(!isFollowing);
  };

  const handleCloseUserPopup = () => {
    setShowUserPopup(false);
    setUserRecipes([]);
  };

  return (
    <section className="HomePage">
      <div className="HomeMainSlider">
        {data.map((item, index) => (
  <div key={index} className="HomeCard">
    <header className="HomeCardHeader">
      <div className="HomeCardHeaderTitleRating">
        <img
          src={item.userimg}
          alt="profilepic"
          className="HomeCardHeaderPic"
          onClick={() => handleUserImageClick(index)}
        />
        <h1 className="HomeCardHeaderTitle">{item.title}</h1>
        <p>Rating</p>
      </div>
      <img src={banner} alt="save" className="HomeCardSave" />
    </header>
    <div className="HomeCardMainImage">
      <img
        src={item.image}
        alt="main Recipe"
        className="HomeCardMainImage"
        onClick={() => handleImageClick(index)}
      />
    </div>
    <div className="HomeCardDescription">
      <h2>Description: </h2>
      <p>{item.description}</p>
    </div>
  </div>
))}
</div>

<CSSTransition
  in={showPopup}
  timeout={300}
  classNames="Popup"
  unmountOnExit
>
  {/* Popup content */}
</CSSTransition>

<CSSTransition
  in={showUserPopup}
  timeout={300}
  classNames="Popup"
  unmountOnExit
>
  <div className="Popup">
    <div className="UserCArd">
      <div className="UserCArdContent">
        <header className="UserHeaderCard">
          <div className="UserHeaderCardImgName">
            <img src={cardData.userimg} alt="" />
            <h1>{cardData.username}</h1>
          </div>
          <button
            onClick={toggleFollow}
            className="FollowButton"
          >
            {isFollowing ? "Unfollow" : "Follow"}
          </button>
          <h2
            onClick={handleCloseUserPopup}
            className="CardCloseButton"
          >
            close
          </h2>
        </header>
        <hr />
        {userRecipes.length > 0 ? (
          <div className="RecipeHomePopupCards">
            {userRecipes.map((recipe, index) => (
              <div key={index} className="RecipeHomePopupCard">
                <img
                  src={recipe.image}
                  alt=""
                  className="RecipeHomePopupCardImg"
                />
                <h1>{recipe.title}</h1>
              </div>
            ))}
          </div>
        ) : (
          <p>No recipes found.</p>
        )}
      </div>
    </div>
  </div>
</CSSTransition>
</section>
);
}

export default Home;
