import { useState, useEffect } from "react";

import axios from "axios";
import "./RecipeCreate.css";
import { useNavigate } from "react-router-dom";
function CreateRecipe() {
  const [categories, setCategories] = useState([]);
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    ingredients: "",
    steps: "",
    category: "",
    image: "",
  });
  const navigate = useNavigate();

  async function PostRecipe() {
    const userId = localStorage.getItem("id");
    const formDataToSend = new FormData();
    formDataToSend.append("title", formData.title);
    formDataToSend.append("description", formData.description);
    formDataToSend.append("ingredients", formData.ingredients);
    formDataToSend.append("steps", formData.steps);
    formDataToSend.append("category", formData.category);
    formDataToSend.append("image", formData.image);

    try {
      await axios.post(
        `http://localhost:1112/recipe/${userId}`,
        formDataToSend,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );
      setFormData({
        title: "",
        description: "",
        ingredients: "",
        steps: "",
        category: "",
        image: "",
      });
      navigate("/profile");
    } catch (err) {
      console.error(err);
    }
  }
  const handleSubmit = async (e) => {
    e.preventDefault();
    await PostRecipe();
  };

  // fetching categories for the option input
  useEffect(() => {
    async function fetchCategories() {
      try {
        const res = await axios.get("http://localhost:1112/category");
        setCategories(res.data.categories);
      } catch (err) {
        console.error(err);
      }
    }
    fetchCategories();
  }, []);

  const handleChange = (e, value) => {
    const name = e.target.name;
    value = e.target.type === "file" ? e.target.files[0] : e.target.value;
    console.log(name);
    if (name === "category") {
      const selectedIndex = e.target.selectedIndex - 1;
      const selectedOption = categories[selectedIndex];
      console.log(selectedOption);
      if (selectedOption) {
        setFormData((prevData) => ({
          ...prevData,
          [name]: selectedOption._id,
        }));
      }
    } else {
      setFormData((prevData) => ({
        ...prevData,
        [name]: value,
      }));
    }
  };

  return (
    <section className="RecipeCreateSection">
      <form onSubmit={handleSubmit}>
        <h1>Post a Recipe</h1>
        <input
          className="title-recipe-form"
          type="text"
          name="title"
          placeholder="Title"
          onChange={handleChange}
          value={formData.title}
          required
          maxLength="25"
        />
        <input
          className="description-recipe-form"
          type="text"
          name="description"
          placeholder="Description"
          onChange={handleChange}
          value={formData.description}
          maxLength="250"
          required
        />
        <input
          className="ingredients-recipe-form"
          type="text"
          name="ingredients"
          placeholder="ingredients"
          onChange={handleChange}
          value={formData.ingredients}
          required
        />
        <input
          className="steps-recipe-form"
          type="text"
          name="steps"
          placeholder="steps"
          onChange={handleChange}
          value={formData.steps}
          required
        />
        <label htmlFor="file-input">Choose an image</label>
        <input
          className="image-recipe-form"
          type="file"
          accept="image/*"
          name="image"
          id="file-input"
          onChange={handleChange}
          style={{ display: "none" }}
          required
        />

        <select
          className="my-select-category"
          name="category"
          onChange={handleChange}
          value={formData.category}
          required
        >
          <option value="chooseCategory"> Choose a Category</option>
          {categories.map((category) => (
            <option key={category._id} value={category._id}>
              {category.title}
            </option>
          ))}
        </select>
        <button className="btn-add-recipe" type="submit">
          Create Recipe
        </button>
      </form>
      <div className="DemoRecipe">
        <h1> Tips </h1>
        <p>
          So you can provide one of the most understanding and easy to read
          Recipes.
        </p>
        <h4>Title</h4>
        <textarea
          type="text"
          value="For the title just put the name of the plate/spice/sauce..."
        />
        <h4>Description</h4>

        <textarea
          type="text"
          value="Some keywords for your description are 'Spicy' 'Sour' 'Sweet' 'Hot' 'Cold' 'Savory' 'Tangy' 'Bitter' 'Umami' 'Zesty' 'Rich' 'Creamy' 'Smoky' 'Tart' 'Mild' 'Fresh' 'Robust' 'Aromatic' 'Bold' 'Nutty' 'Fruity' 'Succulent' 'Juicy' 'Crispy'  "
        />
        <h4>Ingredients</h4>

        <textarea
          type="text"
          value="separate the ingredients with a semi-colon '&#59;' and don't forget to put the measurements in square brackets []. WITHOUT THE SEMI-COLON YOUR INGREDIENTS WILL BE ON THE SAME BULLET POINT"
        />
        <h4>Steps</h4>

        <textarea
          type="text"
          value="let your steps be clear and understandable. "
        />
      </div>
    </section>
  );
}
export default CreateRecipe;
