import { useState,useEffect } from "react";
import "./Login.css";
// import useToken from "./useToken";
import "react-toastify/dist/ReactToastify.css";
import { NavLink } from "react-router-dom";
import axios from "axios";

function Signup() {
  // const { setToken } = useToken();
  const [user, setUser] = useState([]);
  const [imagee, SetImagee] = useState([]);
  const [error, setError] = useState([]);
  const [valid, setValid] = useState(false);


  const [formData, setFormData] = useState({
    _id: "",
    name: "",
    email: "",
    password: "",
    image: "",
  });

  const registerUser = async (userData) => {
    try {
      const formDataToSend = new FormData();
      formDataToSend.append("name", userData.name);
      formDataToSend.append("email", userData.email);
      formDataToSend.append("password", userData.password);
      formDataToSend.append("image", imagee);

      const res = await axios.post(
        "http://localhost:1112/user",
        formDataToSend
      );

      if(!res.data.success){
        setError(res.data.message);
        setValid(true)
      }
      else{
        setUser((prevRents) => [...prevRents, res.data.data]);
      }

    } catch (err) {

      if (err.response && err.response.data) {

        setError(err.response.data.message);
        setValid(true)
      } 
      // else {
      //   console.log("zeinab ",err);
      // }
    }
  };


  const handleSignUp = async (e) => {
    e.preventDefault();
    registerUser(formData);
  };

  const handleImageChange = (e) => {
    e.preventDefault();
    SetImagee(e.target.files[0]);

  };
  useEffect(() => {
    let timer;
    if (valid) {
      timer = setTimeout(() => {
        setValid(false);
      }, 4000);}
    })

  return (
    <div className="Login">
      <div className="Loginbox">
        <NavLink to="/" className="logo loginLogo"></NavLink>
        <h2 className="Logintitle">Sign up</h2>
        {valid ? <i className="ErrorMessageLogin">{error}</i> : null  }

        <form action="#" className="LoginForm">
          <div className="LoginInputs">
            <div className="input-field">
              <input
                type="file"
                name="Image"
                className="Logininput"
                onChange={handleImageChange}
              />
            </div>
            <div className="input-field">
              <input
                type="text"
                placeholder="Name"
                className="Logininput"
                onChange={(e) =>
                  setFormData({ ...formData, name: e.target.value })
                }
              />
            </div>
            <div className="input-field">
              <input
                type="text"
                placeholder="Email"
                className="Logininput"
                onChange={(e) =>
                  setFormData({ ...formData, email: e.target.value })
                }
              />
            </div>
            <div className="input-field">
              <input
                type="password"
                placeholder="Password"
                className="Logininput"
                onChange={(e) =>
                  setFormData({ ...formData, password: e.target.value })
                }
              />
            </div>
          </div>
          <div className="LoginButton-txt">
            <input
              type="submit"
              value="Sign Up"
              className="btn solid LoginButton"
              onClick={handleSignUp}
            />
            <p>
              Already have an account?
              <NavLink to="/" className="signupInLoginTxt">
                {" "}
                Login.
              </NavLink>
            </p>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Signup;
