import { useState } from "react";
import './Login.css'
import useToken from './useToken';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Navigate } from 'react-router';
import axios from 'axios'

async function loginUser(credentials) {
  return fetch('http://localhost:1112/user/login', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(credentials)
  })
    .then(response => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      return response.json();
    })
    .then(data => {
      localStorage.setItem('token', data.token);
      localStorage.setItem('isAdmin', data.isAdmin);
      return data;
    })
    .catch(error => {
      console.error('Error:', error);
      throw error;
    });
}




function Login() {
  const [isSignupMode, setIsSignupMode] = useState(false);
  const [email, setEmail] = useState('');
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const { setToken } = useToken();
  const [isLoggedIn, setIsLoggedIn] = useState(false);



  const handleSigninClick = () => {
    setIsSignupMode(false);
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const response = await loginUser({
        email,
        password,
        name
      });
      setToken(response.token);
      localStorage.setItem('name', response.name);
      localStorage.setItem('token', response.token);
      localStorage.setItem('isAdmin', response.isAdmin);
      localStorage.setItem('loggedIn', 'true');
      toast.success('Logged in successfully!');
      setIsLoggedIn(true);
    } catch (error) {
      console.error('Error:', error);
      toast.error('Wrong Username or Password!!');
    }
  }

  if (isLoggedIn) {
    const isAdmin = localStorage.getItem('isAdmin') === 'true';
    if (isAdmin) {
      return <Navigate to="/dashboard" />;
    } else {
      return <Navigate to="/" />;

    }
  }

  return (
    <div className="Login">

      <form action="#" className="LoginFrom">
            <h2 className="title">Sign in</h2>
            <label for="emailLogin"></label>
              <input type="email" placeholder="Email" id="emailLogin" required onChange={(e) => setEmail(e.target?.value)} />
              <label for="passwordLogin"></label>

              <input type="password" placeholder="Password" id="passwordLogin" required onChange={(e) => setPassword(e.target?.value)} />

            <input type="submit" for="" value="Login" className="btn solid" onClick={handleSubmit} />
          </form> 
      </div>      

  );


}
export default Login;
