import { Routes, Route, Navigate } from "react-router-dom";
//Pages
import Home from "../pages/Home/Home";
import Search from "../pages/Search/Search";
import Challenge from "../pages/Challenge/Challenge";
import Profile from "../pages/Profile/Profile";
import Login from "../pages/Login/Login/Login";
import Signup from "../pages/Login/Signup/Signup";
import Dashboard from "../dashboard/Dashboard";
//css
import "./Navbar.css";
import { toast } from "react-toastify";
import MyRecipes from "../pages/Profile/MyRecipe";

function PageRoutes() {
  const isAdmin = window.localStorage.getItem("isAdmin") === "true";

  const checkAdminAccess = (element) => {
    if (isAdmin) {
      return element;
    } else {
      const pathName = window.location.pathname;
      const isDashboardPage = pathName.startsWith("/dashboard");
      const isDashboardErrorActive = toast.isActive("dashboard-error");

      if (isDashboardPage && !isDashboardErrorActive) {
        toast.error("You don't have access to this part of the website", {
          toastId: "dashboard-error",
        });
      }

      return <Navigate to="/dashboard" replace />;
    }
  };
  return (
    <Routes>
      <Route path="/" element={<Home />} />
      <Route path="/Challenge" element={<Challenge />} />
      <Routes>
        <Route path="Profile" element={<Profile />} />
        <Route path="/Prfile" element={<MyRecipes />} />
      </Routes>
      <Route path="/Search" element={<Search />} />
      <Route path="/Login" element={<Login />} />
      <Route path="/Signup" element={<Signup />} />
      <Route path="/dashboard/*" element={checkAdminAccess(<Dashboard />)} />
    </Routes>
  );
}

export default PageRoutes;
