import axios from "axios";
import { useState, useEffect } from "react";
import { CSSTransition } from "react-transition-group";
import "./MyRecipe.css";
function MyRecipes() {
  const [data, setData] = useState([]);
  const [Data, setCardData] = useState([]);
  const [showPopup, setShowPopup] = useState(false);
  const userId = localStorage.getItem("id");
  useEffect(() => {
    const fetchData = async () => {
      const response = await axios.get(`http://localhost:1112/recipe`);

      await Promise.all(
        response.data.map((item) =>
          axios.get(`http://localhost:1112/user/${item.user}`)
        )
      );

      const userId = localStorage.getItem("id");
      const userRecipes = response.data.filter((item) => item.user === userId);

      setData(userRecipes);
    };
    fetchData();
  }, [userId]);
  async function DeleteRecipe() {
    const userId = localStorage.getItem("id");

    try {
      await axios.post(
        `http://localhost:1112/recipe/${userId}`,
        formDataToSend,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );
      setFormData({
        title: "",
        description: "",
        ingredients: "",
        steps: "",
        category: "",
        image: "",
      });
      navigate("/profile");
    } catch (err) {
      console.error(err);
    }
  }
  const handleImageClick = (index) => {
    setShowPopup(true);
    setCardData(data[index]);
  };

  const handleClosePopup = () => {
    setShowPopup(false);
  };
  return (
    <div className="MyRecipeProfileSectiom">
      {data.map((item, index) => (
        <div key={index} className="MyRecipeCard">
          <p className="MyRecipeCardTitle">{item.title}</p>
          <img
            src={item.image}
            alt="main Recipe"
            className="MyRecipeCardImage"
            onClick={() => handleImageClick(index)}
          />
        </div>
      ))}
      <CSSTransition
        in={showPopup}
        timeout={300}
        classNames="Popup profileCard"
        unmountOnExit
      >
        <div className="Popup profileCard">
          <div className="RecipeCArd">
            <img
              src={Data.image}
              alt="Card"
              className="RecipeCardPopImage profileCardImage"
            />
            <div className="RecipeCArdContent profileCardContent">
              <header className="UserHeaderCard">
                <h2 onClick={handleClosePopup} className="CardCloseButton">
                  Close
                </h2>
                <div className="UserHeaderCardEditDelete">
                  <h2 onClick={handleClosePopup} className="CardCloseButton">
                    Edit
                  </h2>
                  <h2 onClick={handleClosePopup} className="CardCloseButton">
                    Delete
                  </h2>
                </div>
              </header>
              <hr />
              <h1>{Data.title}:</h1>
              <p>{Data.description}</p>
              <hr />
              <h1>Ingredients:</h1>
              {Data.ingredients &&
                Data.ingredients.map((ingredient, index) => (
                  <li key={index}>{ingredient}</li>
                ))}
              <hr />
              <h1>How to apply the recipe:</h1>
              <p>{Data.steps}</p>
            </div>
          </div>
        </div>
      </CSSTransition>
    </div>
  );
}

export default MyRecipes;
